import React, { useRef,us } from 'react'

function AVLMenu(props) {
	const insertRef = useRef(null)
	const removeRef = useRef(null)
	const searchRef = useRef(null)

	const insert = () => {
		props.insert(insertRef.current.value)
	}

	const remove = () => {
		props.remove(removeRef.current.value)
	}

	const search = () => {
		props.search(searchRef.current.value)
	}

	const clear = () => {
		props.random(0)
	}
	const[traversalList,setTraversal]=useState([]);

	return (
		<div className="menu">
			<ul id="mainAVL">
				<li>
					<input
						placeholder="Insert"
						ref={insertRef}
						type="number"
						onKeyUp={e => {
							if (e.keyCode === 13) insert()
						}}
					/>
					<button className="insert" onClick={insert}>
						Insert
					</button>
				</li>
				<li>
					<input
						placeholder="Search"
						type="number"
						ref={searchRef}
						onKeyUp={e => {
							if (e.keyCode === 13) search()
						}}
						className={props.seaError}
					/>
					<button onClick={search}>Search</button>
				</li>
				<li>
					<input
						placeholder="Delete"
						ref={removeRef}
						type="number"
						onKeyUp={e => {
							if (e.keyCode === 13) remove()
						}}
						className={props.delError}
					/>
					<button className="delete" onClick={remove}>
						Delete
					</button>
				</li>
				<li>
					<button onClick={clear}>Clear</button>
				</li>

				<li className="dropdown">
					<button>Traversal</button>
					<div className="dropdown-content">
						<ul>
						<li>
								<button onClick={() =>{
									setTraversal([]);
									props.tree.inorder(traversalList);
									props.setList(traversalList);
									props.traversalName("Inorder:");
									}}>
									Inorder
								</button>
							</li>
							<li>
								<button onClick={() => {
									setTraversal([]);
									props.tree.preorder(traversalList);
									props.setList(traversalList);
									props.traversalName("Preorder:");
									}}>
									Preorder
								</button>
							</li>
							<li>
								<button onClick={() =>{
									setTraversal([]);
									props.tree.postorder(traversalList);
									props.setList(traversalList);
									props.traversalName("Postorder:");
									}}>
									Postorder
								</button>
							</li>
						</ul>
					</div>
				</li>
			</ul>
		</div>
	)
}

export default AVLMenu
